    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
                try {
            double precio = Double.parseDouble(txtPrecio.getText().trim());
            int cantidad = Integer.parseInt(txtCantidad.getText().trim());

            if (precio > 0 && cantidad > 0) {
                CompraProducto compra = new CompraProducto(precio, cantidad);

                double importe = compra.calcularImporteCompra();
                double desc = compra.calcularDescuento();
                double pagar = compra.calcularImportePagar();
                int lapiceros = compra.calcularLapiceros();

                txtSalida.setText("\tResultados");
                txtSalida.append("\n---------------------------------");
                txtSalida.append("\nImporte de compra: S/ " + String.format("%.2f", importe));
                txtSalida.append("\nDescuento: S/ " + String.format("%.2f", desc));
                txtSalida.append("\nImporte a pagar: S/ " + String.format("%.2f", pagar));
                txtSalida.append("\nLapiceros de obsequio: " + lapiceros);
            } else {
                JOptionPane.showMessageDialog(this, "Los valores deben ser mayores a 0.");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese datos v√°lidos.");
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

